property.code:
var PhenoTips = (function(PhenoTips) {
  var widgets = PhenoTips.widgets = PhenoTips.widgets || {};
  widgets.JSONImportDialog = Class.create({
    initialize : function(jsonButton) {
      var _this = this;

      this.$body = $('body');
      this.mainDiv = new Element('div', {'class': 'import-selector'});
      this.jsonData = new Element("textarea", {
        "id": "import",
        "placeholder": "$!{escapetool.javascript($services.localization.render('phenotips.patientData.jsonImportDialog.textarea.placeholder'))}",
        "value": "",
        "class": "import-textarea"
      });
      this.mainDiv.insert(this.jsonData);

      this._initFilePicker();

      this._initServicePicker(jsonButton);

      // Open the modal once the import button is clicked.
      jsonButton.observe("click", function (event) {
        _this.openModal(event);
      }.bindAsEventListener(this));

      var buttons = new Element('div', {'class' : 'buttons import-block-bottom'});

      buttons.insert(new Element('input',
        {type: 'button',
          name : 'import',
          'value': "$escapetool.javascript($services.localization.render('phenotips.patientData.buttons.import'))",
          'class' : 'button',
          'id': 'import_button'
        }).wrap('span', {'class' : 'buttonwrapper'}));

      buttons.insert(new Element('input',
        {type: 'button',
          name : 'cancel',
          'value': "$escapetool.javascript($services.localization.render('phenotips.patientData.buttons.cancel'))",
          'class' : 'button secondary'})
        .wrap('span', {'class' : 'buttonwrapper'}));

      this.mainDiv.insert(buttons);

      var cancelButton = buttons.down('input[name="cancel"]');
      cancelButton.observe('click', function() {
        _this.hide();
      });

      var importButton = buttons.down('input[name="import"]');
      importButton.observe('click', function() {
        _this._importJsonData();
      });

      this.textareaChangeFunction = function() {
        importButton.disabled = _this.jsonData.value.trim() == "";
      };

      // different browsers require different event for onchange detection (special cases: IE, paste, backspace)
      var eventList = ["input","propertychange", "mousemove", "keyup"];
      eventList.each(function(item) {
        _this.jsonData.observe(item, _this.textareaChangeFunction);
      });
    },

    importServices : {
      'single-patient-import' : {
        'service' : XWiki.contextPath + '/rest/patients/' + XWiki.currentDocument.page + '?method=PUT',
        'modalTitle' : "$escapetool.javascript($services.localization.render('phenotips.patientData.jsonImportDialog.singleImport.title'))",
        'callback' : function (event, importDialog) {
          event.stop();
          if (typeof event.findElement('.editbody') !== "undefined") {
            // We are in edit mode.
            var importAfterSave = function() {
              document.stopObserving('xwiki:document:saved', importAfterSave);
            };
            importDialog.dialog.show();
            document.observe('xwiki:document:saved', importAfterSave);
            document.fire('xwiki:actions:save', {'continue' : true, 'form' : event.findElement('form')});
          } else {
            // View mode
            importDialog.dialog.show();
          }
          importDialog.patientId = event.target.getAttribute("content");
        }
      },
      'multi-patient-import' : {
        'service' : XWiki.contextPath + 'rest/patients',
        'modalTitle' : "$escapetool.javascript($services.localization.render('phenotips.patientData.jsonImportDialog.multiImport.title'))",
        'callback' : function (event, importDialog) {
           event.stop();
           importDialog.dialog.show();
        }
      }
    },

    handleFileUpload : function(files) {
      // Handles file upload
      for (var i = 0, numFiles = files.length; i < numFiles; i++) {
        var nextFile = files[i];
        console.log("loading file: " + nextFile.name + ", size: " + nextFile.size);

        var fr = new FileReader();
        var _this = this;
        fr.onload = function(e) {
          _this.jsonData.value = e.target.result;
          _this.textareaChangeFunction();
        };
        fr.readAsText(nextFile);
      }
    },

    openModal : function(event) {
      if (!this.dialog) {
        this.createDialog();
      }
      this._builder.callback(event, this);
    },

    createDialog : function() {
      var closeShortcut = ['Esc'];
      this.dialog = new PhenoTips.widgets.ModalPopup(this.mainDiv,
        {close: {method : this.hide.bind(this), keys : closeShortcut}},
        {extraClassName: "import-dialog", title: this._builder.modalTitle, displayCloseButton: true});
    },

    _importJsonData : function() {
      var importVal = this.jsonData.value.strip();

      this.hide();

      this._updatePatientValues(importVal);
    },

    _updatePatientValues : function (importVal) {
      var _this = this;

      var notificationMsg = "$escapetool.javascript($services.localization.render('phenotips.patientData.jsonImportDialog.longWaitWarning'))";
      this.$body._x_notification = new XWiki.widgets.Notification(notificationMsg, "inprogress");

      new Ajax.Request(_this._builder.service, {
        method: 'POST',
        parameters: importVal,
        contentType : 'application/json',
        onSuccess: function() {
          var notificationMsg = "$escapetool.javascript($services.localization.render('phenotips.patientData.jsonImportDialog.importSuccess'))";
          _this.$body._x_notification.replace(new XWiki.widgets.Notification(notificationMsg, "done"));
          window.location.reload();
        },
        onFailure: function(response) {
          var failureMsg = _this._generateFailureReason(response.status);
          _this.$body._x_notification.replace(new XWiki.widgets.Notification(failureMsg, "error"));
        }
      });
    },

    _initFilePicker : function() {
      if (!!window.FileReader && !!window.FileList) {
        // Show upload button if the browser supports FileReader/DOM File API
        var uploadFileSelector = new Element('input', {"type" : "file", "id": 'jsonInputFile', "style": 'display:none'});
        var _this = this;

        uploadFileSelector.observe('change', function(event) {
          _this.handleFileUpload(this.files);
          try {
            this.value = "";  // clear file selector
          } catch (err) {
            // some older browsers do not allow setting value of a file input element and may generate a security error
          }
        });

        var fileUploadWrapper = new Element('div', {'class' : 'file-select-block-wrapper'});

        var uploadButtonName = "$escapetool.javascript($services.localization.render('phenotips.patientData.buttons.importFileSelect'))";

        var uploadPromptString = "$escapetool.javascript($services.localization.render('phenotips.patientData.jsonImportDialog.sectionTitles.import'))";

        var uploadButton = '<input class="button file-select-button" type="button" value="' + uploadButtonName + '"></input>'

        var re = /___placeholder___/gi;
        uploadPromptString = uploadPromptString.replace(re, uploadButton);

        fileUploadWrapper.insert(new Element('label', {'class': 'import-label'})
          .update(uploadPromptString));

        fileUploadWrapper.down("input.file-select-button").observe('click', function() {
          var fileElem = document.getElementById("jsonInputFile");
          fileElem.click();
        });

        this.mainDiv.insertBefore(fileUploadWrapper, this.jsonData).insert(uploadFileSelector);
      } else {
        this.mainDiv.insert(new Element('div', {'class': 'box infomessage import-warning-nofileupload'})
          .update("$escapetool.javascript($services.localization.render('phenotips.patientData.jsonImportDialog.fileUploadNotSupported'))"));
      }
    },

    _initServicePicker : function(jsonButton) {
      for (var service in this.importServices) {
        // Find the correct import service
        if (jsonButton.hasClassName(service)) {
          this._builder = this.importServices[service];
        }
      }
    },

    _generateFailureReason : function(status) {
      var response;

      switch (status) {
        case 304 :
          response = "$escapetool.javascript($services.localization.render('phenotips.patientData.jsonImportDialog.notModified'))";
          break;
        case 401 :
          response = "$escapetool.javascript($services.localization.render('phenotips.patientData.jsonImportDialog.notAuthenticated'))";
          break;
        case 403 :
          response = "$escapetool.javascript($services.localization.render('phenotips.patientData.jsonImportDialog.notAuthorized'))";
          break;
        case 500 :
          response = "$escapetool.javascript($services.localization.render('phenotips.patientData.jsonImportDialog.patientCreateFailed'))";
          break;
        case 404 :
          response = "$escapetool.javascript($services.localization.render('phenotips.patientData.jsonImportDialog.noPatientRecord'))";
          break;
        default :
          response = "$escapetool.javascript($services.localization.render('phenotips.patientData.jsonImportDialog.patientCreateFailed'))";
      }
      return response;
    },

    hide: function() {
      this.jsonData.value = "";
      this.dialog.closeDialog();
    }
  });

  var init = function(event) {
    ((event && event.memo.elements) || [$('body')]).each(function(element) {
      (element.hasClassName("json-import") ? [element] : element.select(".json-import")).each(function(button) {
        if (!button.__jsonImporter) {
          button.__jsonImporter = new PhenoTips.widgets.JSONImportDialog(button);
        }
      });
    });
    return true;
  };

  (XWiki.domIsLoaded && init()) || document.observe("xwiki:dom:loaded", init);
  document.observe("xwiki:dom:updated", init);

  // End augmentation.
  return PhenoTips;
}(PhenoTips || {}));

.import-dialog {
  width: 65%;
}

.import-warning-nofileupload {
  margin-bottom: 1em;
}

.import-label {
  margin-bottom: .8em;
  font-weight: bold;
}

.import-selector .import-textarea {
  height: 12em;
  resize: vertical;
  margin: 1em auto;
}

.file-select-button {
  margin-left: .5em !important;
}
