            resultsParameter : "rows",
            resultValue : "name",
            tooltip: 'ordo-disease-info',
            markFreeText: true,
            freeTextTooltipHint: "$!escapetool.javascript($services.localization.render('phenotips.patientSheetCode.termSuggest.nonStandardDisorder'))"
        }
    };
    var pickerSpecialClassOptions = {
      'defaultPicker' : {},
      'quickSearch' : {
                  'showKey' : false,
                  'showDeleteTool' : false,
                  'enableSort' : false,
                  'showClearTool' : false,
                  'inputType': 'checkbox',
                  'listInsertionElt' : $(document.documentElement).down('.phenotype-info div.phenotype-group.catch-all .phenotype-other'),
