document.observe('xwiki:dom:loaded', function() {

  var fetchGeneDetails = function(geneRefs, geneNames, isSuggestSelected) {
    if (!geneNames || geneNames == null) {
      geneNames = geneRefs.pluck('value');
    }
    var i = 0;
    var notmapped = 'notmapped';
    geneRefs.each(function (element) {
      if (element.next() && element.next(1) && element.next(1).hasClassName('xHelpButton gene-info'))
          element.next().next('span.xHelpButton').remove();
      if (element.next() && element.next().hasClassName('xHelpButton'))
          element.next('span.xHelpButton').remove();
      if (element.up().down() && element.up().down().hasClassName('fa-exclamation-triangle'))
          element.up().down().remove();
      if (geneNames[i] != "") {
        element._name = geneNames[i];
        var infoTool = new Element('span', {'class' : 'fa fa-info-circle xHelpButton gene-info', 'title' : geneNames[i]});
        var icon_helpController = new PhenoTips.widgets.HelpButton(infoTool);
        var triangle = new Element('span', {'class': "fa fa-exclamation-triangle", title: '$services.localization.render('phenotips.patientSheetCode.termSuggest.nonStandardGenesymbol')'}).update(' ');
        if (isSuggestSelected) {
          element.removeClassName(notmapped);
          element.insert({after: infoTool});
        } else if (/^ENSG[0-9]{11}$/.test(geneNames[i])) {
          element.addClassName(notmapped);
        } else {
          element.addClassName(notmapped);
          element.up().insert({top: triangle});
        }
        ++i;
      }
    });
  }

  var updateInfo = function(el, id, value, isSuggestSelected) {
    value = value || el.value;
    if (el.next() && el.next().className.startsWith('LV_validation_message'))
        el.next().remove();

    //Hide/show variant add button if empty/non-empty genesymbol
    if (el.up('tr').next() && el.up('tr').next().hasClassName('variant-footer')) {
      if (value != "") {
        if (el.__Gene_validator && el.__Gene_validator.valid)
          el.up('tr').next().toggleClassName('v-collapsed', false);
        else
          el.up('tr').next().toggleClassName('v-collapsed', true);
      } else {
        el.up('tr').next().toggleClassName('v-collapsed', true);
      }
    }
    //update genesymbol label for input field
    el.previous().innerHTML = value;
    // update geneid label for the hidden input field
    el.next("input.gene-id").value = id || value;
    //update info icon
    fetchGeneDetails([el],[value], isSuggestSelected);
  }

  // Fetch gene details, marking any non-standardized ids.
  $$('input.gene-name').each(function(elt) {
    fetchGeneDetails([elt], null, !elt.className.endsWith('notmapped'));
  });

  $$('input.gene-name').invoke('observe', "ms:suggest:selected", function (event) {
    if (event.memo) {
      updateInfo(event.element(), event.memo.id, event.memo.value, !event.memo.custom);
    }
  });

  $$('input.gene-name').invoke('observe', "ms:suggest:not-selected", function (event) {
    updateInfo(event.element(), null, null, false);
  });

  // validate gene symbol after best matched was found by suggest widget in case no suggestons was selected
  document.observe("ms:suggest:clearSuggestions", function(event) {
    if (event.memo && event.memo.suggest && event.memo.suggest.fld) {
      var inputGene = event.memo.suggest.fld;
      inputGene.__validation && inputGene.__validation.validate();
    }
  });

  document.observe("xwiki:dom:updated", function(event) {
    if (event.memo && event.memo.elements) {
      event.memo.elements.each(function (elt) {
        if (!elt.className.endsWith('suggestList')){
          fetchGeneDetails(elt.select('input.gene-name'), false);
          var inputGene = elt.select('input.gene-name');
          inputGene.invoke('observe', "ms:suggest:selected", function (event) {
            updateInfo(event.element(), event.memo.id, event.memo.value, !event.memo.custom);
          });
          inputGene.invoke('observe', "ms:suggest:not-selected", function (event) {
            updateInfo(event.element(), null, null, false);
          });
        }
      });
    }
  })
})

document.observe("xwiki:dom:loaded", function(event) {
  var geneSuggestOptions = {
            script: XWiki.contextPath + "/rest/vocabularies/hgnc/suggest?",
            noresults: "$services.localization.render('phenotips.UIXField.genes.noMatches')",
            resultsParameter : "rows",
            resultId : "ensembl_gene_id",
            forceFirstId : true,
            resultValue : 'symbol',
            resultAltName : "alias_symbol",
            tooltip : 'gene-info',
            resultInfo : {},
            matchScript : XWiki.contextPath + "/rest/vocabularies/hgnc/"
  };

  // -----------------------------------------------------------------
  // Create and attach the suggests
  // -----------------------------------------------------------------
  var attachPickers = function() {
    if (typeof(PhenoTips.widgets.Suggest) != "undefined") {
      $$('input.suggest-gene').each(function(item) {
        if (!item.hasClassName('initialized')) {
          item._suggest = new PhenoTips.widgets.Suggest(item, geneSuggestOptions);
          item.addClassName('initialized');
        }
      });
    }
  };
  attachPickers();
  document.observe("xwiki:dom:updated", attachPickers);
});

