property.code:
document.observe('xwiki:dom:loaded', function() {
    var pubmedArticleServiceURL = "https://eutils.ncbi.nlm.nih.gov/entrez/eutils/esummary.fcgi?db=pubmed&retmode=json&method=get&id=";
    var pubmedIdInput = $$('.solved__pubmed_id input')[0];

    if (XWiki.contextaction == "edit" && pubmedIdInput) {
        pubmedIdInput.observe('blur', function () {
            var pubmedId = pubmedIdInput.value;
            if (pubmedId.length < 1) {
                return;
            }
            makeCorsRequest(pubmedId);
        });
        pubmedIdInput.observe('focus', function () {
            pubmedIdInput.next()  && pubmedIdInput.next().remove();
        });
    };

    var createCORSRequest = function(method, url) {
        var xhr = new XMLHttpRequest();
        if ("withCredentials" in xhr) {
           // XHR for Chrome/Firefox/Opera/Safari.
          xhr.open(method, url, true);
        } else if (typeof XDomainRequest != "undefined") {
          // XDomainRequest for IE.
          xhr = new XDomainRequest();
          xhr.open(method, url);
        } else {
          // CORS not supported.
          xhr = null;
        }
        return xhr;
    };

    // Make the actual CORS request
    var makeCorsRequest = function (pubmedId) {
        var url = pubmedArticleServiceURL + pubmedId;

        var xhr = createCORSRequest('GET', url);
        if (!xhr) {
            // CORS not supported
            return;
        }
        //xhr.setRequestHeader('Content-Type', 'application/json');

        // Response handlers.
        xhr.onload = function() {
            var response = JSON.parse(xhr.responseText);
            if (response && response.result  && response.result.uids && response.result.uids.size() > 0) {
                var data = response.result[response.result.uids[0]];
                var title = data.title || '';
                var source = data.source ? data.source + '. ' : '';
                var pubdate = data.pubdate ? data.pubdate + '; ' : '';
                var authors = data.authors || [];
                var pages = data.pages ? data.pages + '. ' : ' ';
                var volume = data.volume || '';
                var issue = data.issue ? '(' + data.issue + ')' : '';
                var elocationid = data.elocationid ? data.elocationid + '. ' : '';
                var epubdate = data.epubdate ? 'Epub ' + data.epubdate + '. ': '';
                var meta = source + pubdate + volume + issue + ':' + pages + elocationid + epubdate;

                var container = new Element("div", {"class" : "article-info"});
                var summary = new Element("p", {"class" : "article-summary"}).update(meta);
                var titleEl = new Element("div", {"class" : "article-title"}).update(new Element("a", {"href" : "https://www.ncbi.nlm.nih.gov/pubmed/?term=" + pubmedId, "target" : "_blank"}).insert(title));

                var idsEl = new Element("div", {"class" : "article-ids"});
                if (data.articleids && data.articleids.length > 0) {
                    for (var i=0; i < data.articleids.length; i++) {
                        if (data.articleids[i].idtype == "pubmed") {
                            idsEl.insert(new Element("span", {"class" : "p-id"}).update("PMID: " + data.articleids[i].value));
                        }
                        if (data.articleids[i].idtype == "doi") {
                            idsEl.insert(new Element("span", {"class" : "p-id"}).update("DOI: " + data.articleids[i].value));
                        }
                    }
                }
                var authorsEl = new Element("div", {"class" : "article-authors"});
                if (authors.length > 0) {
                    var authorsStr = authors[0].name;
                    for (var i=1; i < authors.length; i++) {
                        authorsStr += ', ' + authors[i].name;
                    }
                    authorsStr += '.';
                    authorsEl.insert(authorsStr);
                }
                container.insert(summary).insert(titleEl).insert(authorsEl).insert(idsEl);
                if (XWiki.contextaction == "view") {
                    pubmedIdValue.hide();
                    pubmedIdValue.up().insert(container);
                }
                if (XWiki.contextaction == "edit") {
                    pubmedIdInput.up().insert(container);
                }
            } else {
                if (XWiki.contextaction == "edit") {
                    pubmedIdInput.up().insert(new Element("div", {"class" : "invalid"}).update("Invalid Pubmed ID"));
                }
            }
        };

        xhr.onerror = function() {
          return;
        };

        xhr.send();
    };

    var pubmedIdValue = $$('.solved__pubmed_id .displayed-value p')[0];
    if (XWiki.contextaction == "view" && pubmedIdValue && pubmedIdValue.innerHTML.length > 0) {
        makeCorsRequest(pubmedIdValue.innerHTML);
    };
});

