document.observe('xwiki:dom:loaded', function() {
  var tocLinkClicked = function(event) {
    //event.stop();
    var trigger = event.findElement('a');
    var targetName = trigger && trigger.getAttribute('href');
    var target = targetName && $(targetName.substring(1));
    var parent = target && (target.up('.collapsible-group') || target.up('.chapter'));
    if (parent && parent.hasClassName('collapsed')) {
       parent.removeClassName('collapsed');
       if (parent.down('.expand-tool')) {
         parent.down('.expand-tool').update('â–¼');
       }
       if (parent.up('.chapter')) {
         parent.up('.chapter').removeClassName('collapsed');
       }
       //parent.scrollTo();
    } else {
       //target.scrollTo();
    }
  };

  var tocElement = $('table-of-contents')
  var tocMenuEntry = $('toc-entry');
  if (!tocElement || !tocMenuEntry || !tocElement.down('ul')) {
    tocMenuEntry && tocMenuEntry.up('li').remove();
    return;
  }
  tocMenuEntry = tocMenuEntry.up('li');
  tocMenuEntry.down('ul').replace(tocElement.down('ul'));

  tocMenuEntry.down('ul').select('a').invoke('observe', 'click', tocLinkClicked);
});

document.observe('xwiki:dom:loaded', function() {
  var deleteTool = $('prActionDelete');

  if (!deleteTool) {
    return false;
  }

  var familyInterfaceDoc = new XWiki.Document('FamilyPedigreeInterface', 'PhenoTips');
  var baseUrl = familyInterfaceDoc.getURL('get', 'rand='+ Math.random());

  deleteTool.observe('click', function(clickEvent) {
    clickEvent.stop();
    var item = clickEvent.element();
    item.blur();
    if (item.disabled) {
      // Do nothing if the button was already clicked and it's waiting for a response from the server.
      return;
    } else {
      var familyID = $('familyIdentifier') ? $('familyIdentifier').value : undefined;
      if (familyID) {
        var fullURL = baseUrl + '&action=familyinfo&document_id=' + familyID;
        new Ajax.Request(fullURL, {
          method: "POST",
          onSuccess: onFamilyDataReadyBeforeDeletion.bind(this)
        });
      } else {
        standardPatientDelete();
      }
    }
  });

  var onFamilyDataReadyBeforeDeletion = function (response) {
    if (response && response.responseJSON) {

      var familyMembers = response.responseJSON.family.hasOwnProperty("familyMembers") ? response.responseJSON.family.familyMembers: [];
      var familyId = response.responseJSON.family.id;
      var familyExternalId = response.responseJSON.family.externalId;

      if (familyMembers.length > 1) {
        standardPatientDelete();
      } else {
        // check if the user has delete rights to delete family
        new Ajax.Request(baseUrl, {
            method: "POST",
            parameters: {'action': 'candeletefamily', 'family_id' : familyId, 'deleteallmembers' : false},
            onSuccess: function(response) {
              if (response && response.responseJSON) {
                // remove patient from family OR delete family dialog
                var familyString = (familyExternalId == "") ? familyId + " " : familyExternalId + " (" + familyId + ") ";
                var confirmationText = "$services.localization.render('phenotips.patientRecord.deleteWarning')" + "<br><br>" +
                                       "$services.localization.render('phenotips.patientRecord.pedigree.deleteFamilyWarning')" + " " + familyString + "." +
                                       "$services.localization.render('phenotips.patientRecord.pedigree.deleteFamilyWarningQuestion')" + "<br><br>";
                var parameters = {"action": "deletefamily", "removemembers": true, "family_id": familyId};
                standardPatientDelete(confirmationText, familyId);
              } else {
                standardPatientDelete();
              }
            }
         });
      }

    }
  }

  var standardPatientDelete = function (confirmationText, familyId) {
    var item = $('prActionDelete');
    var url = item.readAttribute('href') + "?confirm=1&form_token=" + $$('meta[name=form_token]')[0].content + (Prototype.Browser.Opera ? "" : "&ajax=1");
    if (confirmationText)
      var text = confirmationText;
    else
      var text = "$services.localization.render('core.recyclebin.confirm')";
    var confirmationDialog = new XWiki.widgets.ConfirmedAjaxRequest(
      /* Ajax request URL */
      url,
      /* Ajax request parameters */
      {
        onCreate : function() {
          // Disable the button, to avoid a cascade of clicks from impatient users
          item.disabled = true;
        },
        onSuccess : function() {
          // Go to space WebHome
          window.location = new XWiki.Document('WebHome', XWiki.Document.currentSpace).getURL('view');
        },
        onFailure : function() {
          // Better luck next time!
          item.disabled = false;
        }
      },
      /* Interaction parameters */
      {
        confirmationText: text
      }
    );

    if (confirmationText) {
      // this is the last patient, add one more button
      var deleteFamilyButton = new Element('input', {type: 'button', 'value': '$services.localization.render('phenotips.patientRecord.deletePatientAndFamily')', 'class' : 'button delete-family', 'id': 'delete_family'});
      var deletePatientButton = $$('input:[type=button][title="(Enter)"]')[0];
      deletePatientButton.addClassName('delete-family');
      deletePatientButton.value = "$services.localization.render('phenotips.patientRecord.deletePatient')";
      var cancelButton = $$('input:[type=button][title="(Esc)"]')[0];
      cancelButton.value = "$services.localization.render('phenotips.patientRecord.deleteWarning.cancelButton')";
      deletePatientButton.up().insert({'after' : (deleteFamilyButton.wrap('span', {'class' : 'buttonwrapper'}))});

      deleteFamilyButton.observe("click", function(event) {
          event.stop();
          confirmationDialog.closeDialog();
          var parameters = {"action": "deletefamily", "removemembers": true, "family_id": familyId, "form_token": $$('meta[name=form_token]')[0].content};
          sendDeleteRequest(parameters);
     });
    }
  }

  var sendDeleteRequest = function(parameters) {
    new Ajax.Request(baseUrl, {
        method: "POST",
        parameters: parameters,
        onSuccess: function(response) {
            if (response) {
              // Go to space WebHome
              window.location = new XWiki.Document('WebHome', XWiki.Document.currentSpace).getURL('view');
            }
        }
    });
  }

});

document.observe('xwiki:dom:loaded', function() {
  // Only do this in edit mode
  if (XWiki.contextaction != 'edit') { return; }
  var form = $('edit') || $('inline');
  if (!form) {return; }
  var formToken = form.form_token && form.form_token.value;

  // What the form initially looked like
  var originalFormData = form.serialize();

  // Warn users when they navigate away from this page without submitting the form, if the data changed
  /*window.onbeforeunload = function(uEvent) {
    if (originalFormData != form.serialize()) {
      return "$services.localization.render('phenotips.contentTopMenu.unsavedDataWarning')";
    }
  };
  form.observe('submit', function(event) {
     window.onbeforeunload = function() {};
  });*/

  // Obtain the number of the loaded version
  var originalVersion = ($$("meta[name=version]").length > 0) ? $$("meta[name=version]")[0].content : false;

  // What could happen on cancel:
  var __getRollbackURL = function (version) {
    return XWiki.currentDocument.getURL('rollback', 'rev=' + version + "&confirm=1&form_token=" + formToken);
  }
  var deleteURL =  XWiki.currentDocument.getURL('delete', "confirm=1&form_token=" + formToken);
  var NEW_DOCUMENT = 'none';

  if (originalVersion) {
     var cancelURL = __getRollbackURL(originalVersion);
  }

  var versionProviderURL = XWiki.currentDocument.getRestURL();
  var __getDocumentVersionFromREST = function(xml) {
    var version = xml.getElementsByTagName('version');
    if (version.length > 0) {
       return version[0].firstChild.nodeValue;
    }
    return false;
  };

  // In background, check if the document actually exists; if not, it should be deleted on cancel
  // (non-existing documents have 1.1 in their meta version)
  new Ajax.Request(
            versionProviderURL, {
              method: 'get',
              onSuccess : function (response) {
                // only update if the information was unavailable in the meta section
                if (!originalVersion && (originalVersion = __getDocumentVersionFromREST(response.responseXML))) {
                   cancelURL = __getRollbackURL(originalVersion);
                }
              },
              onFailure : function (response) {
                if (response.statusCode == 404) {
                   //Document missing
                   originalVersion = NEW_DOCUMENT;
                   cancelURL = deleteURL;
                }
              }.bind(this)
            }
      );

  //form.select('input[name=action_cancel]').invoke('stopOserving', 'click');
  form.select('input[name=action_cancel]').invoke('observe', 'click', function (event) {
    // Continue with the default behavior if the cancel action is being forced after form version verification
    // or if at this point we still have no information regarding the original document version
    if (form._isVersionVerified || !originalVersion || !cancelURL) { return; }
    Event.stop(event);
    // Find the current version
    new Ajax.Request(
            versionProviderURL,
            {
              method: 'get',
              onCreate : function() {
                form._isVersionVerified = true;
              },
              onSuccess : function(response) {
                var crtVersion = __getDocumentVersionFromREST(response.responseXML);
                window.onbeforeunload = function() {};
                if (crtVersion && crtVersion != originalVersion) {
                  // Go to the original state (i.e. rollback or delete)
                  window.location = cancelURL;
                } else {
                  //just cancel
                  event.element().click();
                }
              }.bind(this),
              onFailure : function (response) {
                window.onbeforeunload = function() {};
                // Fail silently
                event.element().click();
              }.bind(this),
              on0 : function (response) {
                response.request.options.onFailure(response);
              }
            }
      );
  });
});

(function(){
  var init = function(event) {
    var docContent = $('mainContentArea');
    var docActions = $('patient-record-actions');
    if (docContent && docActions) {
      var daSticky  = new StickyBox(docActions, docContent, {offsetTop : 0});
    }
  };

  (XWiki.domIsLoaded && init()) || document.observe("xwiki:dom:loaded", init);
})();

document.observe("xwiki:dom:loaded", function () {
  var exportLinks = $$(' .export-link');
  exportLinks.invoke('observe', 'click', function (event) {
    event.stop();
    var newWindow = window.open("", "_blank");
    PhenoTips.widgets.FormUtils.getFormState().saveIfFormDirty(function () {
      newWindow.location = event.findElement().href;
    });
  });
});
