property.code:
document.observe("xwiki:dom:loaded", function(event) {

   var newPatientForm = $('patient-creation-form');
   var studiesSectionInput = newPatientForm.down('input[name="studiesSheet"]');
   var projectsSectionInput = newPatientForm.down('input[name="projectsSheet"]');
   if (!newPatientForm || (!studiesSectionInput && !projectsSectionInput)) {
     return;
   }

   var templateSelectionForm = new Element('form', {'action' : newPatientForm.action, 'class' : 'xform'});
   var formDataWrapper = new Element('div');
   templateSelectionForm.insert(formDataWrapper);

   if (projectsSectionInput) {
      if (studiesSectionInput) {
         var hr = new Element('hr');
         formDataWrapper.insert(hr);
      }
      var projectsSection = new Element('div');
      formDataWrapper.insert(projectsSection);
   }
   if (studiesSectionInput) {
      var studiesSection = new Element('div');
      formDataWrapper.insert(studiesSection);
   }

   newPatientForm.select('input[type="hidden"]').each(function(item) {
     if (item.name != 'sheet') {
         formDataWrapper.insert(new Element('input', {type : item.type, name: item.name, value: item.value}));
     }
   });
   var submitButton = new Element('input', {type : 'submit', value : "$services.localization.render('phenotips.chooseProjectStudy.createButton')", 'class' : 'button'});
   formDataWrapper.insert(submitButton);
   var cancelButton = new Element('input', {type : 'button', value : "$services.localization.render('phenotips.chooseProjectStudy.cancelButton')", 'class' : 'button secondary'});
   formDataWrapper.insert(' ').insert(cancelButton);
   var modalBox = new PhenoTips.widgets.ModalPopup(templateSelectionForm, false, {'title': "$services.localization.render('phenotips.chooseProjectStudy.popupTitle')", 'verticalPosition': 'top', 'removeOnClose': false});
   cancelButton.observe('click', function(event) {
     modalBox.closeDialog();
   });

   var onAjaxResponse = function(section) {
      return {
        onCreate : function() {
          section.update('<img src="$xwiki.getSkinFile('icons/xwiki/ajax-loader-large.gif')"/>');
          modalBox.showDialog();
        },
        onSuccess : function(response) {
           section.update(response.responseText);
        },
        onFailure : function(response) {
          var failureReason = response.responseText || response.statusText;
          if (response.statusText == '' /* No response */ || response.status == 12031 /* In IE */) {
             failureReason = "$services.localization.render('phenotips.chooseProjectStudy.failure.noResponse')";
          }
          _this._messages.update(new Element('div', {'class' : 'errormessage'}).update("$services.localization.render('phenotips.chooseProjectStudy.failureMessage')" + ' ' + failureReason));
        },
        on0 : function (response) {
          response.request.options.onFailure(response);
        }
      };
   };

   newPatientForm.observe('submit', function (event) {
     event.stop();

     var studiesDocRef, projectsDocRef;
     if (studiesSectionInput) {
        var studiesDocRef = XWiki.resource.get(studiesSectionInput.value, XWiki.EntityType.DOCUMENT);
        new Ajax.Request(new XWiki.Document(studiesDocRef.name, studiesDocRef.space).getURL('get'), onAjaxResponse(studiesSection));
     }

     if (projectsSectionInput) {
        var projectsDocRef = XWiki.resource.get(projectsSectionInput.value, XWiki.EntityType.DOCUMENT);
        new Ajax.Request(new XWiki.Document(projectsDocRef.name, projectsDocRef.space).getURL('get'), onAjaxResponse(projectsSection));
     }

   });
});
