property.code:
['xwiki:dom:loaded', 'xwiki:dom:updated'].each(function (eventName) {
  document.observe(eventName, function() {
    $$('.hpo-branch .term-entry:not(.initialized) .tooltip .hide-tool').invoke('observe', 'click', function(event) {
      event.findElement('.tooltip').addClassName('invisible');
    });
    $$('.hpo-branch .term-entry:not(.initialized) .info-tool').invoke('observe', 'click', function(event) {
      event.findElement('.hpo-branch').select('.tooltip').invoke('addClassName', 'invisible');
      event.findElement('.term-entry').down('.tooltip').removeClassName('invisible');
    });
    $$('.hpo-branch .term-entry:not(.initialized)').invoke('addClassName', 'initialized');
  });
});
document.observe('xwiki:dom:loaded', function(){
  function canCollapse(sublist){
    return sublist.select('input').every(function(input){
      return !input.checked;
    });
  }
  var notifyMetaSelected = function (data) {
    var isEvent = !!data.findElement;
    var inputElt = isEvent && data.findElement() || data;
    if (inputElt.type == 'radio' && isEvent) {
      $$('input[name="' + inputElt.name + '"]').each(notifyMetaSelected);
    } else {
      Event.fire(document, inputElt.name.startsWith('PhenoTips.PhenotypeMetaClass_') ? 'phenotype-meta:selected' : 'global-phenotype-meta:selected', {
        key : inputElt.value,
        text : inputElt.title,
        enable : inputElt.checked,
        element : inputElt
      });
    }
  };
  $$('.hpo-branch>ul').forEach(function(list){
    // Fire an event when the selection changes
    list.select('input:checked').each(notifyMetaSelected);
    list.select('input').invoke('observe', 'change', notifyMetaSelected);

    // Expand/collapse list elements
    var listElements = list.select('li');
    listElements.forEach(function(listElement){
      var sublist = listElement.down('ul');
      if (!sublist) {return;}
      //attach click handlers
      var expandTool = listElement.down('span.expand-tool');
      var checkInput = listElement.down('input');
      expandTool.observe('click', function(e) {
        e.stop();
        if (sublist.hasClassName('collapsed')) {
          sublist.removeClassName('collapsed');
          expandTool.innerHTML = "▼";
        } else if (canCollapse(sublist)) {
          expandTool.innerHTML = "►";
          sublist.addClassName('collapsed');
        }
      });
      var toggleCollapse = function() {
        if (canCollapse(sublist) && !checkInput.checked) {
          expandTool.innerHTML = "►";
          sublist.addClassName('collapsed');
        } else {
          expandTool.innerHTML = "▼";
          sublist.removeClassName('collapsed');
        }
      };

      toggleCollapse();

      listElement.observe('click', function(e) {
        if (e.target.hasClassName('phenotype-qualifier-info')) {
          //Prevents toggle behaviour when clicking on the info icon
          return;
        }
        toggleCollapse();
      });

    });
  });
});

.hpo-branch ul {
  list-style-type: none;
  margin: 0;
  margin-left: 1em;
}
.hpo-branch .collapsed {
    display: none;
}
.hpo-branch ul ul {
  margin-left: 1.2em;
}
.hpo-branch .hpo-unknown {
  margin-bottom: 0.5em;
}
.hpo-branch .term-entry {
  display: inline-block;
  width: 100%;
  position: relative;
}
.hpo-branch .term-entry .info-tool {
  cursor: help;
}
.hpo-branch .term-entry .expand-tool {
  margin-left: -1em;
  top: 0.3em;
  position: absolute;
}
