property.code:
var ExtraData = (function (ExtraData) {
  var tools = ExtraData.tools = ExtraData.tools || {};
  tools.Editor = Class.create({
    initialize : function () {
      $$('.extradata-list a.delete').invoke('observe', 'click', this.ajaxDeleteData.bindAsEventListener(this));
      $$('.list-actions a.add').invoke('observe', 'click', this.ajaxAddData.bindAsEventListener(this));
    },
    ajaxDeleteData : function (event) {
      event.stop();
      var deleteTrigger = event.element();
      if (deleteTrigger.disabled) {
         return;
      }
      new XWiki.widgets.ConfirmedAjaxRequest(deleteTrigger.href, {
        onCreate : function() {
          deleteTrigger.disabled = true
        },
        onSuccess : function() {
          var dataRow = deleteTrigger.up('tr:not(.head-group)');
          var dataTable = deleteTrigger.up('table.extradata-list');
          if (dataRow) {
            dataRow.remove();
            Event.fire(dataTable, 'extradata:deleted', {'element' : dataRow});
          }
          if (dataTable) {
            var i = 1;
            dataTable.select('th.row-count').each(function(item) {
              item.update((i++));
            });
            Event.fire(document, 'xwiki:dom:updated', {'elements' : [dataTable]});
          }
        },
        onComplete : function() {
          deleteTrigger.disabled = false;
        }
      },
      {
         confirmationText : "$escapetool.javascript($services.localization.render('phenotips.tableMacros.rowDeleteConfirmation'))"
      });
    },
    ajaxAddData : function (event) {
      event.stop();
      var addTrigger = event.element();
      if (addTrigger.disabled) {
         return;
      }
      var classname = '';
      try {
        var classname = /classname=([^&]+)/.exec(addTrigger.href)[1];
      } catch (err) {
         new XWiki.widgets.Notification("$escapetool.javascript($services.localization.render('phenotips.tableMacros.error.typeNotFound'))", 'error');
         return;
      }
      var dataTable = addTrigger.up('.list-actions').previous('table[id^="extradata-list-' + classname + '"]');
      if (!dataTable) {
        new XWiki.widgets.Notification("$escapetool.javascript($services.localization.render('phenotips.tableMacros.listNotFound'))" + ' ' + classname, 'error');
      }
      var propertyName = addTrigger.href.replace(/.*&xredirect=.*propertyName=([^&]*).*/, '$1') || '';
      var propertyValue = addTrigger.href.replace(/.*&xredirect=.*propertyValue=([^&]*).*/, '$1') || '';
      var addedDisplaySheet = addTrigger.href.replace(/.*&xredirect=.*addedDisplaySheet=([^&]*).*/, '$1') || '';
      var url = addTrigger.href.replace(/(&xredirect=[^&]*)/m, '$1' + encodeURIComponent('?xpage=plain&xaction=lastentry&dataClassName=' + classname + '&withLabel=' + dataTable.hasClassName('withLabel') + '&propertyName=' + propertyName + '&propertyValue=' + propertyValue + (addedDisplaySheet ? '&sheet=' + addedDisplaySheet : ''))).replace(/#.*/m, '');
      new Ajax.Request(url, {
        onCreate : function() {
          addTrigger.disabled = true
          addTrigger._x_notif = new XWiki.widgets.Notification("$escapetool.javascript($services.localization.render('phenotips.tableMacros.newEntry.progressNotification'))", 'inprogress');
        },
        onSuccess : function (response) {
          addTrigger._x_notif.hide();
          var newRow = new Element('tr', {'class' : 'new'});
          dataTable.down('tbody').insert(newRow);
          if (dataTable.hasClassName('withCounter')) {
            var idx = dataTable.select('.row-count').size() + 1;
            newRow.insert(new Element('th', {'class' : 'row-count'}).update(idx));
          }
          newRow.insert(response.responseText);
          newRow.down('a.delete').observe('click', this.ajaxDeleteData.bindAsEventListener(this));
          Event.fire(dataTable, 'extradata:added', {'element' : newRow});
          Event.fire(document, 'xwiki:dom:updated', {'elements' : [newRow]});
        }.bind(this),
        onFailure : function(response) {
          var failureReason = response.statusText;
          if (response.statusText == '' /* No response */ || response.status == 12031 /* In IE */) {
            failureReason = 'Server not responding';
          }
          if (addTrigger._x_notif) {
            addTrigger._x_notif.replace(new XWiki.widgets.Notification("$escapetool.javascript($services.localization.render('phenotips.tableMacros.newEntry.error'))" + failureReason, "error"));
          } else {
            new XWiki.widgets.Notification(this.interactionParameters.failureMessageText + failureReason, "error");
          }
        },
        on1223 : function(response) {
          response.request.options.onSuccess(response);
        },
        on0 : function(response) {
          response.request.options.onFailure(response);
        },
        onComplete : function() {
          addTrigger.disabled = false
        }
      });
    }
  });
  return ExtraData;
}(ExtraData || {}));

document.observe('xwiki:dom:loaded', function() {
  new ExtraData.tools.Editor();
})

