          };

          c.update(
                    elt('span', 'info').insert(elt('span', 'key').update(json.id))
                                       .insert(' ')
                                       .insert(elt('span', 'value').update(json.name_translated))
                  );
          json.def_translated && c.insert(elt('p').update(json.def_translated.replace(/\s*\n\s*/, ' ').replace(/`([^`]+)`\s+\(([A-Z]+:[0-9]+)`?\)/g, '<em title="$2">$1</em>')));

