          var advancedInfo = elt('dl');
          for (var l in labels) {
            if (json[l]) {
              advancedInfo.insert(elt('dt', '', labels[l]));
              json[l].each(function (item) {
                advancedInfo.insert(elt('dd', '', item.name_translated || item));
